// src/components/StockList/StockList.jsx
import React, { useState } from 'react';
import styles from './StockList.module.css';
import { useNavigate } from 'react-router-dom';

const StockList = ({ stocks }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [industryFilter, setIndustryFilter] = useState('All');
  const navigate = useNavigate();

  const industries = ['All', ...Array.from(new Set(stocks.map(stock => stock.sector).filter(Boolean)))];

  const filteredStocks = stocks.filter(stock => {
    const matchesSearch = stock.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          stock.symbol.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesIndustry = industryFilter === 'All' || stock.sector === industryFilter;
    return matchesSearch && matchesIndustry;
  });

  const handleView = (symbol) => {
    navigate(`/stock/${symbol}`);
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Stock List</h2>
      <div className={styles.filterContainer}>
        <input
          type="text"
          placeholder="Search by name or symbol"
          className={styles.searchInput}
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <select
          className={styles.dropdown}
          value={industryFilter}
          onChange={e => setIndustryFilter(e.target.value)}
        >
          {industries.map(ind => (
            <option key={ind} value={ind}>{ind}</option>
          ))}
        </select>
      </div>

      <div className={styles.grid}>
        {filteredStocks.map(stock => (
          <div key={stock.symbol} className={styles.card}>
            <h3 className={styles.stockName}>
              {stock.name} <span className={styles.symbol}>({stock.symbol})</span>
            </h3>
            <p className={styles.price}>â‚¹ {stock.latestPrice?.toFixed(2) || 'N/A'}</p>
            <p className={styles.industry}>{stock.sector}</p>
            <button className={styles.button} onClick={() => handleView(stock.symbol)}>
              View Details
            </button>
          </div>
        ))}
        {filteredStocks.length === 0 && (
          <p className={styles.noResult}>No stocks found.</p>
        )}
      </div>
    </div>
  );
};

export default StockList;
