import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [error, setError] = useState(null);

  // Fetch from API or fallback to LocalStorage
  const fetchWatchlist = async () => {
    try {
      const res = await axios.get('/watchlist');
      setWatchlist(res.data);
      localStorage.setItem('watchlist', JSON.stringify(res.data)); // Cache
    } catch (err) {
      setError('Failed to fetch from API. Loading from local storage...');
      const cached = localStorage.getItem('watchlist');
      if (cached) {
        setWatchlist(JSON.parse(cached));
      }
    }
  };

  useEffect(() => {
    fetchWatchlist();
  }, []);

  return (
    <div className="container mt-4">
      <h2 className="mb-4">ðŸ“ˆ Your Watchlist</h2>
      {error && <div className="alert alert-warning">{error}</div>}

      {watchlist.length === 0 ? (
        <p>No stocks in your watchlist.</p>
      ) : (
        <div className="row">
          {watchlist.map(stock => (
            <div key={stock.id} className="col-md-4 mb-3">
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{stock.name} ({stock.symbol})</h5>
                  <p className="card-text">
                    <strong>Sector:</strong> {stock.sector}<br />
                    <strong>Latest Price:</strong> â‚¹{stock.latestPrice?.toFixed(2)}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Watchlist;
