// src/pages/StockDetails.jsx
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import styles from './StockDetails.module.css';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from 'recharts';

const StockDetails = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // If the Home page passed a companyName via state, use it as the initial search term.
  const initialCompanyName = location.state?.companyName || '';

  const [searchTerm, setSearchTerm] = useState(initialCompanyName);
  const [stock, setStock] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [notFound, setNotFound] = useState(false);

  // This function will search for the stock using the company name (or symbol) provided.
  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setNotFound(false);
    setStock(null);
    setLoading(true);

    try {
      // Call the API using the searchTerm
      const res = await fetch(
        `http://localhost:3001/stocks?search=${encodeURIComponent(searchTerm)}`
      );
      const results = await res.json();

      if (!results || results.length === 0) {
        setMessage("No matching stocks found.");
        setLoading(false);
        return;
      }
      // Pick the first match.
      const match = results[0];
      // Fetch detailed info for that stock using its ID.
      fetchStockDetails(match.id);
    } catch (err) {
      console.error(err);
      setMessage("Error searching for stock.");
      setLoading(false);
    }
  };

  // Fetch detailed stock data based on its ID.
  const fetchStockDetails = (stockId) => {
    fetch(`http://localhost:3001/stocks/${stockId}`)
      .then((res) => res.json())
      .then((data) => {
        setStock(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching stock details:", err);
        setNotFound(true);
        setLoading(false);
      });
  };

  // Optionally, if there's an initial search term from navigation state, trigger a search on mount.
  useEffect(() => {
    if (initialCompanyName) {
      handleSearchSubmit({ preventDefault: () => {} });
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialCompanyName]);

  if (loading) {
    return <div className={styles.container}>Loading...</div>;
  }

  if (notFound) {
    return (
      <div className={styles.errorContainer}>
        <h2>Stock Not Found</h2>
        <button onClick={() => navigate('/')}>Back to Home</button>
      </div>
    );
  }

  return (
    <div className={styles.container}>
      <button className={styles.backBtn} onClick={() => navigate('/')}>
        ← Back
      </button>

      {/* Search Bar Section */}
      <form onSubmit={handleSearchSubmit} className={styles.searchForm}>
        <input
          type="text"
          placeholder="Search by company name or symbol..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className={styles.searchInput}
        />
        <button type="submit" className={styles.searchBtn}>
          Search
        </button>
      </form>

      {/* Display messages if any */}
      {message && <p className={styles.message}>{message}</p>}

      {/* Once stock details are loaded, display them */}
      {stock && (
        <>
          <h1 className={styles.heading}>
            {stock.name} <span>({stock.symbol})</span>
          </h1>

          <div className={styles.details}>
            {/* Check if history exists and calculate today's change */}
            {stock.history && stock.history.length > 1 && (() => {
              const latest = stock.history[stock.history.length - 1];
              const prev = stock.history[stock.history.length - 2];
              const change = ((latest.close - prev.close) / prev.close) * 100;
              return (
                <>
                  <p className={styles.price}>
                    Current Price: <strong>₹{latest.close.toFixed(2)}</strong>
                  </p>
                  <p className={change >= 0 ? styles.positive : styles.negative}>
                    {change >= 0 ? '+' : ''}{change.toFixed(2)}% Today
                  </p>
                </>
              );
            })()}
          </div>

          <button className={styles.watchlistBtn} onClick={() => {
            // Add to watchlist API call
            fetch('http://localhost:3001/watchlist', {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ stock_id: stock.id }),
            })
            .then(res => res.ok ? setMessage('⭐ Added to watchlist!') : res.json())
            .then(data => {
              if(data?.error) {
                setMessage(`❌ ${data.error}`);
              }
            })
            .catch(err => {
              console.error(err);
              setMessage('❌ Failed to add to watchlist.');
            });
          }}>
            ⭐ Add to Watchlist
          </button>

          {/* Display the price history chart if available */}
          {stock.history && (
            <div className={styles.chart}>
              <h3>📈 Price History</h3>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart
                  data={stock.history.map((d) => ({
                    date: d.date,
                    price: d.close,
                  }))}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis domain={['auto', 'auto']} />
                  <Tooltip />
                  <Line type="monotone" dataKey="price" stroke="#007bff" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default StockDetails;
