import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StockDetails.module.css';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer
} from 'recharts';

const StockDetails = () => {
  const navigate = useNavigate();
  const { state } = useLocation();
  const { companyName } = state;


  // State variables:
  // selectedSymbol is kept to record the symbol of the current stock (if needed)
  // stock holds the detailed info fetched from the API.
  // message shows success/failure notifications.
  // loading and notFound are used for conditional rendering.
  // searchTerm holds the current input value for the search bar.
  const [selectedSymbol, setSelectedSymbol] = useState('');
  const [stock, setStock] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [notFound, setNotFound] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Function to add the currently displayed stock to the watchlist.
  const handleAddToWatchlist = async () => {
    try {
      const res = await fetch('http://localhost:3001/watchlist', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ stock_id: stock.id }),
      });

      if (res.ok) {
        setMessage('⭐ Added to watchlist!');
      } else {
        const errData = await res.json();
        setMessage(`❌ ${errData.error}`);
      }
    } catch (err) {
      console.error(err);
      setMessage('❌ Failed to add to watchlist.');
    }
  };

  // When the user submits a search, this function is called.
  // It makes a request to the backend /stocks endpoint with a search query,
  // then, if a match is found, it fetches the detailed data for that stock.
  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setNotFound(false);
    setStock(null);
    setLoading(true);

    try {
      // Search for the stock by company name or symbol.
      const res = await fetch(`http://localhost:3001/stocks?search=${encodeURIComponent(searchTerm)}`);
      const results = await res.json();

      if (!results || results.length === 0) {
        setMessage("No matching stocks found.");
        setLoading(false);
        return;
      }
      // For simplicity, we choose the first match.
      const match = results[0];
      setSelectedSymbol(match.symbol);

      // Now load the detailed stock information using the stock ID.
      fetchStockDetails(match.id);
    } catch (err) {
      console.error(err);
      setMessage("Error searching for stock.");
      setLoading(false);
    }
  };

  // Fetch the detailed stock info given the stock's id.
  const fetchStockDetails = (stockId) => {
    fetch(`http://localhost:3001/stocks/${stockId}`)
      .then((res) => res.json())
      .then((data) => {
        setStock(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching stock details:', err);
        setNotFound(true);
        setLoading(false);
      });
  };

  // Conditional rendering based on loading state and notFound flag.
  if (loading) return <div className={styles.container}>Loading...</div>;

  if (notFound) {
    return (
      <div className={styles.errorContainer}>
        <h2>Stock Not Found</h2>
        <button onClick={() => navigate('/')}>Back to Home</button>
      </div>
    );
  }

  return (
    <div className={styles.container}>
      <button className={styles.backBtn} onClick={() => navigate('/')}>← Back</button>

      {/* Search Bar Section */}
      <form onSubmit={handleSearchSubmit} className={styles.searchForm}>
        <input
          type="text"
          placeholder="Search by company name or symbol..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className={styles.searchInput}
        />
        <button type="submit" className={styles.searchBtn}>Search</button>
      </form>

      {/* Display stock details if they have been loaded */}
      {stock && (
        <>
          <h1 className={styles.heading}>
            {stock.name} <span>({stock.symbol})</span>
          </h1>

          <div className={styles.details}>
            {/* Calculate today's change using the last two entries in the stock's history */}
            {stock.history && stock.history.length > 1 && (() => {
              const latest = stock.history[stock.history.length - 1];
              const prev = stock.history[stock.history.length - 2];
              const change = ((latest.close - prev.close) / prev.close) * 100;
              return (
                <>
                  <p className={styles.price}>
                    Current Price: <strong>₹{latest.close.toFixed(2)}</strong>
                  </p>
                  <p className={change >= 0 ? styles.positive : styles.negative}>
                    {change >= 0 ? '+' : ''}{change.toFixed(2)}% Today
                  </p>
                </>
              );
            })()}
          </div>

          {/* Button to add the displayed stock to the watchlist */}
          <button className={styles.watchlistBtn} onClick={handleAddToWatchlist}>
            ⭐ Add to Watchlist
          </button>
          {message && <p className={styles.message}>{message}</p>}

          {/* Display stock's price history using a line chart */}
          {stock.history && (
            <div className={styles.chart}>
              <h3>📈 Price History</h3>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={stock.history.map(d => ({ date: d.date, price: d.close }))}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis domain={['auto', 'auto']} />
                  <Tooltip />
                  <Line type="monotone" dataKey="price" stroke="#007bff" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default StockDetails;
