import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [error, setError] = useState(null);

  // Fetch watchlist on mount
  useEffect(() => {
    fetchWatchlist();
  }, []);

  const fetchWatchlist = async () => {
    try {
      const response = await axios.get('/api/watchlist');
      const uniqueItems = filterUnique(response.data);
      setWatchlist(uniqueItems);
    } catch (err) {
      console.error('Failed to fetch watchlist:', err);
      setError('Failed to load watchlist');
    }
  };

  // Helper to filter out duplicates based on 'id'
  const filterUnique = (arr) => {
    const seen = new Set();
    return arr.filter((item) => {
      if (seen.has(item.id)) return false;
      seen.add(item.id);
      return true;
    });
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/watchlist/${id}`);
      setWatchlist((prev) => prev.filter((item) => item.id !== id));
    } catch (err) {
      console.error('Error: Failed to delete from watchlist');
      setError('Failed to delete item');
    }
  };

  return (
    <div className="watchlist-container">
      <h2>üìà My Watchlist</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {watchlist.map((item, index) => (
          <li key={`${item.id}-${index}`} className="watchlist-item">
            <NavLink
              to={`/stocks/${item.symbol}`}
              className={({ isActive }) =>
                isActive ? 'active-link' : 'inactive-link'
              }
            >
              {item.name} ({item.symbol})
            </NavLink>
            <button onClick={() => handleDelete(item.id)}>‚ùå Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Watchlist;
